classic_textad.css:
*{
  font-size: 13px;
}

body{
}

.ad_image {
  background-repeat: no-repeat;
}

.buttonswrapper {
  float:left;
  display: -webkit-box;
  -webkit-box-orient: horizontal;
  -webkit-box-pack: center;
  -webkit-box-align: center;
  height: 100px;
  margin-right: 12px;
}

.color_title {
  font-family: Helvetica, 'Helvetica Neue', Arial, sans-serif, "Trebuchet MS";
  font-size: 115%;
  color: |color_title|;
  text-decoration: underline;
  line-height: 35px;
  font-weight: bold;
}


.color_description {
  display: block;
  width: 350px;
  font-family: Helvetica, 'Helvetica Neue', Arial, sans-serif, "Trebuchet MS";
  font-size: 95%;
  font-weight: normal;
  color: |color_description|;
  text-decoration: none;
  float:  left;
  line-height: 20px;
  padding: 0px 0px 0px 0px;
}


.color_domain {
  padding: 5px 0px 0px 0px;
  font-family: Verdana, Arial, Helvetica, sans-serif, "Trebuchet MS";
  font-size: 11px;
  color: |color_domain|;
  font-weight: bold;
  float:  left;
  width: 450px;
  line-height: 21px;
}

div.ad_box div.right_side {
	float: left;
	width: 350px;
}

.color_domain {
  color: |color_domain|;
  text-decoration: none;
}

.ad_box {
  margin-bottom: 12px;
	width: 600px;
	float: left;
}

mobile_textad.css:
*{
  font-size: 13px;
}


body{
        background: filter:alpha(opacity=0);
        margin: 0;
        padding: 0;
}
.ad_box{
        margin: 10px 0;
}
.color_description {
        display: block;
        width: 300px;
        font-family: Arial, Helvetica, sans-serif, "Trebuchet MS";
        font-size: 75%;
        font-weight: normal;
        color: |color_description|;
        text-decoration: none;
}
.color_title {
        font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
        font-size: 107%;
        color: |color_title|;
        font-weight: bold;
        text-decoration: underline;
}
.color_domain {
        font-family: Verdana, Arial, Helvetica, sans-serif, "Trebuchet MS";
        font-size: 9px;
        color: |color_domain|;
}
